changeSet.sql:
-- Backup FK violations CREATE TABLE IF NOT EXISTS public.affiliate_mercantile_fk_violation_backup AS SELECT m.* FROM public.affiliate_mercantile m LEFT JOIN public.affiliate a ON a.id_affiliate = m.id_affiliate WHERE a.id_affiliate IS NULL OR m.id_affiliate IS NULL;
-- Delete FK violations DELETE FROM public.affiliate_mercantile m USING ( SELECT m.ctid FROM public.affiliate_mercantile m LEFT JOIN public.affiliate a ON a.id_affiliate = m.id_affiliate WHERE a.id_affiliate IS NULL OR m.id_affiliate IS NULL ) z WHERE m.ctid = z.ctid;
-- Backup duplicates CREATE TABLE IF NOT EXISTS public.affiliate_mercantile_dup_backup AS SELECT * FROM public.affiliate_mercantile WHERE filed_number IN ( SELECT filed_number FROM public.affiliate_mercantile GROUP BY filed_number HAVING COUNT(*) > 1 );
-- Delete duplicate rows keeping the first per filed_number WITH ranked AS ( SELECT ctid, filed_number, ROW_NUMBER() OVER (PARTITION BY filed_number ORDER BY ctid) AS rn FROM public.affiliate_mercantile ) DELETE FROM public.affiliate_mercantile m USING ranked r WHERE m.ctid = r.ctid AND r.rn > 1;
