changeSet.sql:
-- Backup FK violations (null or missing affiliate) CREATE TABLE IF NOT EXISTS public.affiliation_detail_fk_violation_backup AS SELECT d.* FROM public.affiliation_detail d LEFT JOIN public.affiliate a ON a.id_affiliate = d.id_affiliate WHERE a.id_affiliate IS NULL OR d.id_affiliate IS NULL;
-- Delete FK violations DELETE FROM public.affiliation_detail d USING ( SELECT d.ctid FROM public.affiliation_detail d LEFT JOIN public.affiliate a ON a.id_affiliate = d.id_affiliate WHERE a.id_affiliate IS NULL OR d.id_affiliate IS NULL ) z WHERE d.ctid = z.ctid;
-- Backup duplicates by filed_number CREATE TABLE IF NOT EXISTS public.affiliation_detail_dup_backup AS SELECT * FROM public.affiliation_detail WHERE filed_number IN ( SELECT filed_number FROM public.affiliation_detail GROUP BY filed_number HAVING COUNT(*) > 1 );
-- Delete duplicate rows keeping the first per filed_number WITH ranked AS ( SELECT ctid, filed_number, ROW_NUMBER() OVER (PARTITION BY filed_number ORDER BY ctid) AS rn FROM public.affiliation_detail ) DELETE FROM public.affiliation_detail d USING ranked r WHERE d.ctid = r.ctid AND r.rn > 1;
